export declare const tosh: {
    config: {
        useCustomProperties: true;
        initialColorMode: string;
    };
    colors: {
        text: "#000";
        background: "#fff";
        primary: "#000";
        secondary: "#3f3f3f";
        muted: "#e0e0e0";
        highlight: "#9f9f9f";
        gray: "#6c6c6c";
        accent: "#3f3f3f";
        modes: {
            dark: {
                text: "#fff";
                background: "#060606";
                primary: "#d2d2d2";
                secondary: "#b2b2b2";
                muted: "#191919";
                highlight: "#3c3c3c";
                gray: "#999";
                accent: "#e0e0e0";
            };
        };
    };
    fonts: {
        body: "Silom, monospace";
        heading: "Silom, monospace";
        monospace: "Silom, monospace";
    };
    fontSizes: number[];
    fontWeights: {
        body: number;
        heading: number;
        display: number;
    };
    lineHeights: {
        body: number;
        heading: number;
    };
    text: {
        heading: {
            fontFamily: string;
            fontWeight: string;
            lineHeight: string;
        };
        display: {
            variant: string;
            fontSize: number[];
            fontWeight: string;
            letterSpacing: string;
            mt: number;
        };
    };
    styles: {
        Container: {
            p: number;
            maxWidth: number;
        };
        root: {
            fontFamily: "body";
            lineHeight: string;
            fontWeight: string;
        };
        h1: {
            variant: string;
        };
        h2: {
            variant: string;
            fontSize: number;
        };
        h3: {
            variant: string;
            fontSize: number;
        };
        h4: {
            variant: string;
            fontSize: number;
        };
        h5: {
            variant: string;
            fontSize: number;
        };
        h6: {
            variant: string;
            fontSize: number;
        };
        p: {
            fontSize: number;
        };
        a: {
            color: "primary";
            '&:hover': {
                color: "secondary";
            };
        };
        pre: {
            fontFamily: "monospace";
            fontSize: number;
            p: number;
            color: "text";
            bg: "muted";
            borderColor: "text";
            borderStyle: "solid";
            borderTopWidth: number;
            borderLeftWidth: number;
            borderRightWidth: number;
            borderBottomWidth: number;
            overflow: "auto";
            code: {
                color: "inherit";
            };
        };
        code: {
            fontFamily: "monospace";
            fontSize: number;
        };
        inlineCode: {
            fontFamily: "monospace";
            color: "secondary";
            bg: "muted";
            px: number;
        };
        ul: {
            listStyleType: "square";
        };
        table: {
            width: string;
            my: number;
            borderCollapse: "separate";
            borderSpacing: number;
            'th,td': {
                textAlign: "left";
                py: string;
                pr: string;
                pl: number;
                borderColor: "text";
                borderBottomStyle: string;
            };
        };
        th: {
            backgroundColor: "muted";
            verticalAlign: string;
            borderBottomWidth: number;
        };
        td: {
            verticalAlign: string;
            borderBottomWidth: number;
        };
        hr: {
            border: number;
            borderBottom: string;
            borderColor: "text";
        };
    };
};
export default tosh;
