function sendMessage(e,t,n,s){if(e=String(e).trim(),t=String(t).trim(),n=String(n).trim(),s=String(s).trim(),e==""||t==""||n==""||s==""||!String(t).includes("@")||!String(t).includes("."))alert("Please ensure all fields are complete and/or email is valid");else{document.getElementById("submit").disabled=!0;var i,a,o=new Headers;o.append("Content-Type","application/json"),i=JSON.stringify({email:String(t),sender:String(e),subject:String(n),message:String(s)}),a={method:"POST",headers:o,body:i,redirect:"follow"},fetch("https://7g9n4wu4gg.execute-api.us-east-1.amazonaws.com/dev",a).then(e=>e.json()).then(function(){document.getElementById("name").value="",document.getElementById("email").value="",document.getElementById("subject").value="",document.getElementById("message").value="",alert("Thank you for sending your message, "+e+"!"),document.getElementById("submit").disabled=!1}).catch(e=>console.log(e))}}function getVisitorCount(){var t,n,e=new Headers;e.append("Content-Type","application/json"),t=JSON.stringify({}),n={method:"POST",headers:e,body:t,redirect:"follow"},fetch("https://fiokcjyrp1.execute-api.us-east-1.amazonaws.com/dev",n).then(e=>{e.json().then(e=>{var t=document.getElementById("visitors");t.innerHTML="Visitor Count: "+String(e)})})}